asnp citrix*;
Get-BrokerMachine -AdminAddress "controllername" -Filter "(((DNSName -like `"*vdi7*`")) -and (SessionSupport -eq `"SingleSession`"))" -MaxRecordCount 500 -Property @("DNSName","CatalogName","DesktopGroupName","AssociatedUserNames","InMaintenanceMode","PersistUserChanges","PowerState","RegistrationState","MachineName","Uid","DesktopUid","DesktopKind","HypervisorConnectionUid","HypHypervisorConnectionUid","ProvisioningType","PvdStage","LastDeregistrationReason","FaultState","Tags","CatalogUid","DesktopGroupUid","SessionCount","Capabilities","SupportedPowerActions") -ReturnTotalRecordCount -Skip 0 -SortBy "+DNSName" | Export-Csv -Path .\vdiData.csv -NoTypeInformation
